 $(window).scroll(function () {
                if ($(this).scrollTop() > 5) {
                    $(".navbar-me").addClass("fixed-me");
                } else {
                    $(".navbar-me").removeClass("fixed-me");
                }
            });
            

            $(window).scroll(function () {
                if ($(this).scrollTop() > 0) {
                    $('#toTop').fadeIn();
                } else {
                    $('#toTop').fadeOut();
                }
            });

            $('#toTop').click(function () {
                $('body,html').animate({scrollTop: 0}, 800);
            });
            